function startsWith(a, b) {
	let retVal = false;
	let test = a.substring(0, b.length);
	if (test === b) {
		retVal = true;
	}
	return retVal;
}

function getValue(){
    let returnValue = '';    
    if (typeof source.PhoneNumber !== 'undefined' && source.PhoneNumber) { 
        returnValue = source.PhoneNumber.trim()       
        returnValue = returnValue.replace(/[^0-9+]/g, '')
        if (startsWith(returnValue, '06')) {            
			returnValue = returnValue.replace('06', '+316');
        }		
    };

    return returnValue;
}

getValue();

function getExternalId(){
