function GetDepartmentName() {
    let departmentName = "";

    if (typeof sourceContract.DepartmentName !== 'undefined' && sourceContract.DepartmentName) {
        const fullName = sourceContract.DepartmentName;
        const parts = fullName.split('_');
        departmentName = parts.length > 1 ? parts.slice(1).join('_') : fullName;

        departmentName = departmentName.trim();
    }
    
    return departmentName;
}

GetDepartmentName();

